import React from 'react'
var commaNumber = require('comma-number')

function SingleStock (props) {
  // conditionally render color of the stock Symbol and Price 
  // red if current price is less than open price
  // green if reverse
  // gray within $1 difference 
  return (
    <tr>
      <td className={(props.currentPrice > props.openPrice) ? 'green' : (Math.abs(props.price - props.openPrice) < 1) ? 'gray' : 'red'}>
        {props.symbol}
      </td>
      <td>
        {props.shares} shares
      </td>
      <td className={(props.price > props.openPrice) ? 'green' : (Math.abs(props.price - props.openPrice) < 1) ? 'gray' : 'red'}>
        $ {commaNumber((props.shares * props.price).toFixed(2))}
      </td>
    </tr>
  )
}

export default SingleStock
