        axios.get(`https://sandbox.iexapis.com/stable/stock/c/ohlc?token=Tpk_f60d00f3b3774527b14ddc2510d54b18`)
        .then(response => {
          console.log(response)
        })
      })
        // if (response.data.symbol) {
        //   let orderCost = (response.data.latestPrice * quantity).toFixed(2)
        //   let newBalance = this.props.user.cash - orderCost
        //   if (newBalance > 0 ) {
        //     this.setState({
        //       ticker: '',
        //       quantity: ''
        //     })
        //     this.placeOrder(this.props.user.id, newBalance, ticker, quantity, response.data.latestPrice)
        //   } else {
        //     alert("Insufficient funds")
        //     return
        //   }
        // } else {
        //   this.setState({
        //     errors: 'no such ticker',
        //     ticker: '',
        //     quantity: ''
        //   })
        // }
      // })
      // .catch(error => {
      //   console.log('api errors:', error)
      //   alert('Please enter a valid stock ticker symbol')

    })
  }

  handleLogout = () => {
    this.setState({
      isLoggedIn: false,
      user: {}
    })
  }

  render() {
    return (
      <div id='wrapper'>
        <BrowserRouter>
          <Switch>
            <Route 
              exact path='/login' 
              render={props => (
              <Login {...props} handleLogin={this.handleLogin} loggedInStatus={this.state.isLoggedIn}/>
              )}
            />
            <Route 
              exact path='/signup' 
              render={props => (
              <Signup {...props} handleLogin={this.handleLogin} loggedInStatus={this.state.isLoggedIn}/>
              )}
            />
            <Route 
              path='/' 
              render={props => (
              <Home {...props} {...this.state} updateUser={this.updateUser} updateStocks={this.updateStocks} handleLogout={this.handleLogout} userobj={this.state.user} setMode={this.setMode} modeStatus={this.state.mode} loggedInStatus={this.state.isLoggedIn}/>
              )}
            />
          </Switch>
        </BrowserRouter>
      </div>
    );
  }
}

export default App;
